test "filename evaluation" {
    var tmp_dir = std.testing.tmpDir(.{});
    defer tmp_dir.cleanup();

    // Wide and non-wide quoted strings behave the same for UTF-8 text
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ÐºÐ¸Ñ€Ð¸Ð»Ð»Ð¸Ñ†Ð°': FileNotFound" }},
        \\#pragma code_page(65001)
        \\1 RCDATA "ÐºÐ¸Ñ€Ð¸Ð»Ð»Ð¸Ñ†Ð°"
    ,
        null,
        tmp_dir.dir,
    );
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ÐºÐ¸Ñ€Ð¸Ð»Ð»Ð¸Ñ†Ð°': FileNotFound" }},
        \\#pragma code_page(65001)
        \\1 RCDATA L"ÐºÐ¸Ñ€Ð¸Ð»Ð»Ð¸Ñ†Ð°"
    ,
        null,
        tmp_dir.dir,
    );
    // Unquoted too
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ÐºÐ¸Ñ€Ð¸Ð»Ð»Ð¸Ñ†Ð°': FileNotFound" }},
        \\#pragma code_page(65001)
        \\1 RCDATA ÐºÐ¸Ñ€Ð¸Ð»Ð»Ð¸Ñ†Ð°
    ,
        null,
        tmp_dir.dir,
    );
    // Unquoted Windows-1252 should get converted to UTF-8, too
    // (â‚¬ is 0x80 in Windows-1252 but codepoint U+20AC which is encoded as 0xE2 0x82 0xAC in UTF-8)
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'â‚¬â‚¬â‚¬': FileNotFound" }},
        "#pragma code_page(1252)\n1 RCDATA \x80\x80\x80",
        null,
        tmp_dir.dir,
    );
    // Number literals are accepted
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file '-12': FileNotFound" }},
        "#pragma code_page(1252)\n1 RCDATA -12",
        null,
        tmp_dir.dir,
    );

    // Invalid UTF-8 gets converted to ï¿½
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ï¿½ï¿½ï¿½ï¿½': FileNotFound" }},
        "#pragma code_page(65001)\n1 RCDATA \"\xf0\xf0\x80\x80\x80\"",
        null,
        tmp_dir.dir,
    );
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ï¿½ï¿½ï¿½ï¿½': FileNotFound" }},
        "#pragma code_page(65001)\n1 RCDATA L\"\xf0\xf0\x80\x80\x80\"",
        null,
        tmp_dir.dir,
    );
    // Same with unquoted literals
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ï¿½ï¿½ï¿½ï¿½': FileNotFound" }},
        "#pragma code_page(65001)\n1 RCDATA \xf0\xf0\x80\x80\x80",
        null,
        tmp_dir.dir,
    );

    // The max width of escapes is dependent on the string being wide
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file ' AC': FileNotFound" }},
        \\1 RCDATA "\x20AC"
    ,
        null,
        tmp_dir.dir,
    );
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'â‚¬': FileNotFound" }},
        \\1 RCDATA L"\x20AC"
    ,
        null,
        tmp_dir.dir,
    );

    // Codepoints that cannot be encoded in UTF-8 get converted to ï¿½
    try testCompileErrorDetailsWithDir(
        &.{.{ .type = .err, .str = "unable to open file 'ï¿½': FileNotFound" }},
        \\1 RCDATA L"\xD800"
    ,
        null,
        tmp_dir.dir,
    );
}

